={{ 
  (() => {
    const m = $json["html"].match(/form-2\.gif"[^>]*>[\s\S]*?<div[^>]*>([^<]+)<\/div>/);
    if(m){
      const dateStr = m[1].replace(/&#47;/g, '/').trim();
      const [roc, mon, day] = dateStr.split('/');
      return `${parseInt(roc,10)+1911}-${mon}-${day}`;
    }
    return null;
  })()
}}

