const result = [];

const users = items[0].json.users;
const amounts = items[0].json.amount_split;

// 取得 aggregate node 的資料
const aggregateData = $node["Aggregate"].json;
const lineUserIds = aggregateData.line_user_id;
const userNames = aggregateData.who;

for (let i = 0; i < users.length; i++) {
  // 尋找對應的 user name
  const userId = users[i];
  const userIndex = lineUserIds.indexOf(userId);
  const userName = userIndex !== -1 ? userNames[userIndex] : null;

  result.push({
    json: {
      id: userId,
      name: userName,
      amount: amounts[i]
    }
  });
}

return result;

